document.addEventListener('DOMContentLoaded', function(){
    let container = document.getElementById("container");

    // Prendo un logo a caso
    let indiceRandom = Math.floor(Math.random() * fileImmagini.length);
    let logoRandom = fileImmagini[indiceRandom];

    let logoIniziale = document.createElement("img");
    logoIniziale.id = "logo"
    logoIniziale.alt = "logo_corrente";
    logoIniziale.src = "img/" + logoRandom;

    let da_sostituire = document.getElementById("logo");
    container.replaceChild(logoIniziale, da_sostituire);

});

document.getElementById("risposta").addEventListener("keydown", function(event) {
    if (event.key === "Enter") {
        // Ottieni l'elemento immagine
        let logo = document.getElementById("logo");
        
        // Ottieni l'URL dell'immagine
        let urlLogo = logo.src;
        
        // Estrai il nome dell'immagine dall'URL
        let nomeLogo = urlLogo.substring(urlLogo.lastIndexOf('/') + 1);
        
        // Stampa il nome dell'immagine
        console.log("Nome del file:", nomeLogo);
        
        // Prendo il nome vero del logo per validare la risposta dell'utente
        nomeLogo = nomeLogo.split('_');
        let soluzione = nomeLogo[0];
        
        console.log("Nome del logo:", soluzione);

        let risposta = document.getElementById("risposta").value;

        // pulisco la risposta
        document.getElementById("risposta").value = "";

        console.log("Risposta dell'utente: " + risposta);

        if(risposta == soluzione){
            console.log("Esatto!");
            cambiaLogo();
        }
        else{
            console.log("No, riprova");
        }
        
    }

});

function cambiaLogo(){
    // Devo scegliere un immagine random da mostrare
    let indiceRandom = Math.floor(Math.random() * fileImmagini.length);
    let logoRandom = fileImmagini[indiceRandom];
    
    // Crea il percorso completo dell'immagine
    let pathImmagine = "img/" + logoRandom;


    let nuovoLogo = document.createElement("img")
    nuovoLogo.id = "logo";
    nuovoLogo.alt = "logo_corrente";
    nuovoLogo.src = pathImmagine;
    let vecchioLogo = document.getElementById("logo");

    let container = document.getElementById("container");
    container.replaceChild(nuovoLogo, vecchioLogo);
}
